import React, { useState } from 'react'
import "../components/CSS/style.css"

const Tasks = () => {

    const [item, setItem] = useState("");

    const [item2, setItem2] = useState("");

    const [allEntry, setNewEntry] = useState([]);
    const [entrySet2, reSet2] = useState([]);



    /*const handleData = () => {
        let data = { item: item };
        setNewEntry([...allEntry, data]);
        setItem('');
    }*/

    const setInput = (e) =>{
        console.log("button pressssed");
        e.preventDefault();
    }

    
    const handleInput2 = (e) => {
        console.log("button pressed");
        e.preventDefault();
        const data2={item2:item2};
        reSet2([...entrySet2,data2]);
        setItem2("");

    }

 
   
    return (
        <div className="container">
            {/*Sidebar*/}
            <div className="sidebar">

                <div className="heading">
                    <h1>InnerHour</h1>
                </div>

                <div className="items">
                    <ul>
                        <li><i className="fa-solid fa-folder-closed"></i>Task 1</li>
                        <li><i className="fa-solid fa-folder-closed"></i>Task 2</li>

                    </ul>

                </div>
            </div>



            {/*Right Section*/}

            <div className="right-section">



                {/*Middle Section*/}


                <div className="head1">
                    <h2>An Application which will find differences between two lists A & B</h2>
                </div>

                <div className="input">
                  <input type="text" id="text" name="text" placeholder='Add Item'
                  value={item} onChange={(e) =>setItem(e.target.value)}></input>
                  <button className="btn1" onClick={setInput}>Add item</button>

                </div>
     

                <div className="input2">
                    <input type="text" id="item2" name="item2" placeholder="✍ Add Item"
                        value={item2} onChange={(e) => setItem2(e.target.value)}></input>
                    <button className="btn_input1" onClick={handleInput2}>Add Item</button>

                </div>

                <div className="con1">
                    <p>Set A</p>
           <div> 
            {
                    allEntry.map((curElem) => {
                    return (
                      <div className="data-show">
                        <p>  {curElem.item}</p>
                       
                        </div>
                    )
                    }
                    )
                }
                    </div>
                </div>

                <div className="con2">
                    <p className="set-b">Set B</p>

                    <div>
                        {
                            entrySet2.map((elem) => {
                                return (
                                <div className="data-show2"> 
                                    <p className="para"> {elem.item2}</p>
                                </div>
                                )
                            }
                            )
                        }
                    </div>
                </div>
               {/*
                <div>
                    {
                        allEntry.map((curElem) => {
                            return (

                                <div className="showData">
                                    <p>{curElem.item}</p>

                                </div>
                            )
                        })
                    }
                </div>
                 */}

                <div className="middle-head">
                    <button className="btn2" >Compute</button>
                    <i className="fa-solid fa-plus"></i>
                </div>





            </div>
        </div>

    )
}

export default Tasks
