import React, { useState, useEffect } from 'react'
import "../components/CSS/style.css"
import { Scrollbars } from 'react-custom-scrollbars-2';

const Tasks = () => {

    const [set, setData] = useState([]);

    const fetchApi = async () => {

        const res = await fetch("https://api.facthunt.in/fostergem/v1/post/list/public");
        console.log(set);
        setData(await res.content.json());


    }

    useEffect(() => {
        fetchApi();

    }, [setData]);



    return (

        <>
            <div className="container">
                {/*Sidebar*/}
                <div className="sidebar">

                    <div className="heading">
                        <h1>NextTales Inc</h1>
                    </div>

                    <div className="items">
                        <ul>
                            <li><i className="fa-solid fa-folder-closed"></i>Profile</li>
                            <li><i className="fa-solid fa-folder-closed"></i>Discover</li>

                        </ul>

                    </div>
                </div>

                {/*Right Section*/}

                <div className="right-section">


                    <div className="parent_cards">
                        <Scrollbars>
                            <div className="contain">
                                {
                                    set.map((curElem) => {
                                        console.log(curElem)
                                        const {summary,title,userId,date} = curElem
                                       
                                        
                                        return <>
                                            <div className="cards">
                                                <img src={summary} alt="img"></img>
                                                <div className="info">
                                                    <div className="name">
                                                        <h3 style={{ marginRight: "2rem" }}><span>NAME - </span>{title}</h3>

                                                    </div>
                                                    <div className="gender">
                                                        <h3><span>GENDER - </span>{date}</h3>
                                                    </div>
                                                </div>
                                                <div className="bio">
                                                    <p className="biodata"><span style={{ fontWeight: "bold" }}>QUALIFICATION - </span>
                                                        {userId}
                                                    </p>
                                                </div>

                                            </div>
                                        </>


                                    })
                                }

                            </div>


                        </Scrollbars>


                    </div>


                </div>
            </div>
        </>
    )
}

export default Tasks
