import React, { useState, useEffect } from 'react'
import "../components/CSS/style.css"

const Tasks = () => {

    const [set, setData] = useState([]);

    const fetchApi = async () => {

        const res = await fetch("https://api.facthunt.in/fostergem/v1/post/list/public");
        console.log(set);
        setData(await res.json());
        

    }

    useEffect(() => {
        fetchApi();

    },[setData]);



    return (

        <>
        <div className="container">
            {/*Sidebar*/}
            <div className="sidebar">

                <div className="heading">
                    <h1>NextTales Inc</h1>
                </div>

                <div className="items">
                    <ul>
                        <li><i className="fa-solid fa-folder-closed"></i>Task 1</li>
                        <li><i className="fa-solid fa-folder-closed"></i>Task 2</li>

                    </ul>

                </div>
            </div>

            {/*Right Section*/}

            <div className="right-section">


                <div className="parent_cards">
                      
                             {
                                set.map((curElem) => {
                                    return  <>
                                        <div className="cards">
                                        <img src={curElem.postId} alt="img"></img>
                                        <div className="info">
                                            <div className="name">
                                                <h3 style={{ marginRight: "2rem" }}><span>NAME - </span>{curElem.userId}</h3>

                                            </div>
                                            <div className="gender">
                                                <h3><span>GENDER - </span>{curElem.date}</h3>
                                            </div>
                                        </div>
                                        <div className="bio">
                                            <p className="biodata"><span style={{ fontWeight: "bold" }}>QUALIFICATION - </span>
                                            {curElem.postId}
                                            </p>
                                        </div>

                                    </div>
                                    </>
                                    

                                })
                             }
                                
                                  
                                
                            



                </div>


            </div>
        </div>
        </>
    )
}

export default Tasks
