import React from 'react'

const userdata = () => {

    const [set, setData] = useState([]);


    useEffect(() => {
        const fetchApi = async () => {
            const url = "https://api.facthunt.in/fostergem/v1/post/list/public"
            const response = await fetch(url);

            const dataJson = await response.json();
            // console.log(dataJson.content);
            setData(dataJson.content);
        }

        fetchApi();



    }, [setData])
    return (
        <>
            {
            set.map((curItem) => {
          const {coverImageUrl} = curItem;

                <div className="cards" >
                    <img src={coverImageUrl} alt="img"></img>
                    <div className="info">
                        <div className="name">
                            <p className="nameh3" style={{ marginRight: "6.9rem" }}><span style={{ fontWeight: "bold" }}>NAME - </span>{coverImageUrl}</p>

                        </div>
                        <div className="gender">
                            <p className="gendershow"><span style={{ fontWeight: "bold" }}>GENDER - </span>{coverImageUrl}</p>
                        </div>
                    </div>
                    <div className="bio">
                        <p className="biodata"><span style={{ fontWeight: "bold" }}>QUALIFICATION - </span>
                            {coverImageUrl}
                        </p>
                    </div>

                </div>
            })
            }

        </>
    )
}

export default userdata
