import React, { useState, useEffect } from 'react'
import "../components/CSS/style.css"
import { Scrollbars } from 'react-custom-scrollbars-2';
import Userdata from "./Userdata"

const Tasks = () => {

    const [set, setData] = useState([]);

    useEffect(() => {
        const fetchApi = async () => {
            const url = "https://api.facthunt.in/fostergem/v1/post/list/public"
            const response = await fetch(url);

            const dataJson = await response.json();
            // console.log(dataJson.content);
            setData(dataJson.content);
        }

        fetchApi();



    }, [setData])



    return (

        <>
            <div className="container">
                {/*Sidebar*/}
                <div className="sidebar">

                    <div className="heading">
                        <h2>NextTales Inc</h2>
                    </div>

                    <div className="items">
                        <ul>
                            <li><i className="fa-solid fa-folder-closed" style={{marginRight:"2rem"}}></i>Profile</li>
                            <li><i className="fa-solid fa-folder-closed"></i>Discover</li>

                        </ul>

                    </div>

                    <div className="social-web">
                        
                            <a href="https://www.facebook.com/"><i className="fa-brands fa-facebook-f" ></i></a>
                            <a href="https://twitter.com/?lang=en-in"><i className="fa-brands fa-twitter"></i></a>
                            <a href="https://www.linkedin.com/home"><i className="fa-brands fa-linkedin"></i></a>
                            <a href="https://www.linkedin.com/home">  <i class="fa-regular fa-envelope-dot"></i></a>
                       

                    </div>
                </div>

                {/*Right Section*/}

                <div className="right-section">

                 <div className="right-sec-head">
                    <p>Discover</p>
                 </div>
                    <div className="parent_cards">
                        <Scrollbars>
                            <div className="contain">
                                {
                                    set.map((curElem) => {

                                        const { user } = curElem
                                        
                                        return <>
                                              <Userdata {...curElem}/>
                                            {/* <div className="cards" >
                                                <img src={user.profilePic} alt="img"></img>
                                                <div className="info">
                                                    <div className="name">
                                                        <p className="nameh3" style={{ marginRight: "2rem" }}><span style={{fontWeight:"bold"}}>NAME - </span>{user.fname}</p>

                                                    </div>
                                                    <div className="gender">
                                                        <p className="gendershow"><span style={{fontWeight:"bold"}}>GENDER - </span>{user.gender}</p>
                                                    </div>
                                                </div>
                                                <div className="bio">
                                                    <p className="biodata"><span style={{ fontWeight: "bold" }}>QUALIFICATION - </span>
                                                        {user.bio}
                                                    </p>
                                                </div>

                                            </div> */}
                                        </>


                                    })
                                }

                            </div>


                        </Scrollbars>


                    </div>


                </div>
            </div>
        </>
    )
}

export default Tasks
