import React, { useState, useEffect } from 'react'
import "../components/CSS/style.css"
import { Scrollbars } from 'react-custom-scrollbars-2';

const Tasks = () => {

    const [set, setData] = useState([]);

    useEffect(() => {
        const fetchApi = async () => {
            const url = "https://api.facthunt.in/fostergem/v1/post/list/public"
            const response = await fetch(url);

            const dataJson = await response.json();
            // console.log(dataJson.content);
            setData(dataJson.content);
        }

        fetchApi();



    }, [setData])



    return (

        <>
            <div className="container">
                {/*Sidebar*/}
                <div className="sidebar">

                    <div className="heading">
                        <h1>NextTales Inc</h1>
                    </div>

                    <div className="items">
                        <ul>
                            <li><i className="fa-solid fa-folder-closed" style={{marginRight:"2rem"}}></i>Profile</li>
                            <li><i className="fa-solid fa-folder-closed"></i>Discover</li>

                        </ul>

                    </div>
                </div>

                {/*Right Section*/}

                <div className="right-section">


                    <div className="parent_cards">
                        <Scrollbars>
                            <div className="contain">
                                {
                                    set.map((curElem) => {

                                        const { user ,postId} = curElem
                                        console.log(postId)
                                        return <>
                                            <div className="cards" key={postId}>
                                                <img src={user.profilePic} alt="img"></img>
                                                <div className="info">
                                                    <div className="name">
                                                        <p className="nameh3" style={{ marginRight: "2rem" }}><span style={{fontWeight:"bold"}}>NAME - </span>{user.fname}</p>

                                                    </div>
                                                    <div className="gender">
                                                        <h3 className="gendershow"><span>GENDER - </span>{user.gender}</h3>
                                                    </div>
                                                </div>
                                                <div className="bio">
                                                    <p className="biodata"><span style={{ fontWeight: "bold" }}>QUALIFICATION - </span>
                                                        {user.bio}
                                                    </p>
                                                </div>

                                            </div>
                                        </>


                                    })
                                }

                            </div>


                        </Scrollbars>


                    </div>


                </div>
            </div>
        </>
    )
}

export default Tasks
