import React, { useState, useEffect } from 'react'
import "../components/CSS/style.css"

const Tasks = () => {

    const [data, setData] = useState([]);

    const fetchApi = async () => {

        const res = await fetch("https://api.facthunt.in/fostergem/v1/post/list/public");
      
        setData(await res.json());
        console.log(data);

    }

    useEffect(() => {
        fetchApi();

    },[]);



    return (

        <>
        <div className="container">
            {/*Sidebar*/}
            <div className="sidebar">

                <div className="heading">
                    <h1>InnerHour</h1>
                </div>

                <div className="items">
                    <ul>
                        <li><i className="fa-solid fa-folder-closed"></i>Task 1</li>
                        <li><i className="fa-solid fa-folder-closed"></i>Task 2</li>

                    </ul>

                </div>
            </div>

            {/*Right Section*/}

            <div className="right-section">


                <div className="parent_cards">
                  


                           

                                
                                    <div className="cards">
                                        <img src="https://wallpapercave.com/wp/3wskH6G.jpg" alt="img"></img>
                                        <div className="info">
                                            <div className="name">
                                                <h3 style={{ marginRight: "2rem" }}><span>NAME - </span>Agrim</h3>

                                            </div>
                                            <div className="gender">
                                                <h3><span>GENDER - </span>male</h3>
                                            </div>
                                        </div>
                                        <div className="bio">
                                            <p className="biodata"><span style={{ fontWeight: "bold" }}>QUALIFICATION - </span>
                                            Masters of journalism & mass communication from Jadhavpur University
                                            </p>
                                        </div>

                                    </div>
                                
                            



                </div>


            </div>
        </div>
        </>
    )
}

export default Tasks
