import React, { useState } from 'react'
import "../components/CSS/style.css"

const Tasks = () => {

    const [item, setItem] = useState(" ");
    const [item1,setItem2] = useSate("");
    const [allEntry, setNewEntry] = useState([]);


    
    const handleData = () => {
        let data = { item: item };
        setNewEntry([...allEntry, data]);
        setItem('');
    }

    return (
        <div className="container">
            {/*Sidebar*/}
            <div className="sidebar">

                <div className="heading">
                    <h1>InnerHour</h1>
                </div>

                <div className="items">
                    <ul>
                        <li><i class="fa-solid fa-folder-closed"></i>Task 1</li>
                        <li><i class="fa-solid fa-folder-closed"></i>Task 2</li>




                    </ul>

                </div>
            </div>



            {/*Right Section*/}

            <div className="right-section">



                {/*Middle Section*/}


                <div className="head1">
                    <h2>An Application which will find differences between two lists A & B</h2>
                </div>

                <div className="input">
                    <input type="text" id="item" name="item" placeholder="✍ Add Item" 
                    value={item} onChange={(e) => setItem(e.target.value)}></input>
                    <button className="btn1">Add Item</button>

                </div>

                <div className="input2">
                    <input type="text" id="item" name="item" placeholder="✍ Add Item" 
                    value={item} onChange={(e) => setItem(e.target.value)}></input>
                    <button className="btn_input1">Add Item</button>

                </div>

                <div>
                   {
                    allEntry.map((curElem) =>{
                     return (
                     
                        <div className="showData"> 
                         <p>{curElem.item}</p>

                        </div>

                        
                     )
                    })
                   }
                </div>

                <div className="middle-head">
                    <button className="btn2" onClick={handleData}>Compute</button>
                    <i class="fa-solid fa-plus"></i>
                </div>





            </div>
        </div>

    )
}

export default Tasks
