import React, { useState, useEffect } from 'react'
import "../components/CSS/style.css"
import { Scrollbars } from 'react-custom-scrollbars-2';

const Tasks = () => {

    const [set, setData] = useState([]);

    const fetchApi = async () => {

        const res = await fetch("https://assessment.api.vweb.app/products");
        console.log(set);
        setData(await res.json());
        

    }

    useEffect(() => {
        fetchApi();

    },[setData]);



    return (

        <>
        <div className="container">
            {/*Sidebar*/}
            <div className="sidebar">

                <div className="heading">
                    <h1>NextTales Inc</h1>
                </div>

                <div className="items">
                    <ul>
                        <li><i className="fa-solid fa-folder-closed"></i>Profile</li>
                        <li><i className="fa-solid fa-folder-closed"></i>Discover</li>

                    </ul>

                </div>
            </div>

            {/*Right Section*/}

            <div className="right-section">

            <Scrollbars>
                <div className="parent_cards">
                      
                             {
                                set.map((curElem) => {
                                    const { product_id, selling_price, name, stock } = curElem
                                    return  <>
                                        <div className="cards">
                                        <img src={product_id} alt="img"></img>
                                        <div className="info">
                                            <div className="name">
                                                <h3 style={{ marginRight: "2rem" }}><span>NAME - </span>{selling_price}</h3>

                                            </div>
                                            <div className="gender">
                                                <h3><span>GENDER - </span>{name}</h3>
                                            </div>
                                        </div>
                                        <div className="bio">
                                            <p className="biodata"><span style={{ fontWeight: "bold" }}>QUALIFICATION - </span>
                                            {stock}
                                            </p>
                                        </div>

                                    </div>
                                    </>
                                    

                                })
                             }
                                
                                  
                                
                            



                </div>
                </Scrollbars>

            </div>
        </div>
        </>
    )
}

export default Tasks
